##SMP 10-12-2022

file_obj = open('/Users/student/programming_repository/files/pset_files/Python_06.txt', 'r')

for line in file_obj:
	line = line.upper()
	print(line)

out_file = open('/Users/student/programming_repository/files/pset_files/Python_06_uc.txt', 'w')

dna = ''


unique_nts = set(dna) ## define each nt ID in the seq (A T G C N if present)
nt_count = {} ## create empty dict for associating the nt ID with its count 

for nt in dna:
	if nt in unique_nts:
		count = dna.count(nt)
		nt_count[nt] = count

nt_comp = nt_count['A'] + nt_count['T'] + nt_count['C'] + nt_count['G']
gc_content = ((nt_count['G'] + nt_count['C'])/ nt_comp) * 100

out_file.write(str(nt_comp) + '\n' + str(gc_content))
print("File written!")


